import { Link } from "react-router-dom";
import { useState } from "react";
import { DivPass } from "../../Login/styles";
import { FormRegister } from "../style";
import Header from "../../../components/header";
import axios from "axios";
import ContainerS from "../../../components/container/style";

const RegisterUser = () => {
  const [visible, setVisible] = useState(true);

  const [loding, setLoding] = useState(false)
  
 
  const visibilityPass = () => {
    const pass = document.getElementById("password");
    const eye = document.getElementById("eye");

    if (visible) {
      pass.type = "text";
      eye.src =
        "data:image/svg+xml;base64,";
      setVisible(false);
    } else {
      pass.type = "password";
      eye.src =
        "data:image/svg+xml;base64,";
      setVisible(true);
    }
  };

  const HandleSubmit = (event) => {
    event.preventDefault();
    const name = event.target.name.value;
    const email = event.target.email.value;
    const password = event.target.password.value;
    const passwordConfirmation = event.target.passwordConfirmation.value;

  
    
    if (password !== passwordConfirmation) {
      alert("senhas n√£o conferem");
    }
    if (password.length < 6) {
      alert("Sua senha deve ter mais que 6 caracteres!");
    } else {
      const user = {
        name,
        email,
        password,
        passwordConfirmation,
        role: "USER",
      };
      console.log(user);

      axios
        .post("/user", user)
        .then((res) => {
          alert("Cadastrado com sucesso");
          console.log(res);
        
        })
        .catch((e) => alert(e.message));
 
        
    }
  };

  return (
    <>
      <Header />
      <ContainerS>
        <FormRegister method="POST" onSubmit={HandleSubmit}>
          <h1>cadastro </h1>

          <label htmlFor="name">Nome</label>
          <input
            id="name"
            type="text"
            placeholder="Digite aqui seu nome"
            name="name"
            required
          />

          <label htmlFor="email">Email</label>
          <input
            id="email"
           
            type="email"
            placeholder="Digite aqui seu email "
            required
          />

          <label htmlFor="password">Senha</label>
          <DivPass>
            <button onClick={visibilityPass} type="button">
              <img
                id="eye"
                src="data:image/svg+xml;base64,"
                alt="VisiblePassword"
                width="30"
                height="24"
              />
            </button>
            <input
              id="password"       
              type="password"
              placeholder="Digite aqui sua senha "
              required
            />
          </DivPass>

          <label htmlFor="passwordConfirmation">Senha de Confirma√ß√£o</label>
          <input
            id="passwordConfirmation"
            type="password"
            placeholder="Digite aqui a senha de confirma√ß√£o"
            required
          />

          <div className="group-btn">
            <Link to="/">
              <button type="button" className="btnRegister">
                Voltar
              </button>
            </Link>

            <button type="submit" className="btnRegister">
              Salvar
            </button>
          </div>
        </FormRegister>
      </ContainerS>
    </>
  );
};
export default RegisterUser;
